llday_start = "";$allday_end = "";$i = 0;foreach($contents as $line) {	if(strstr($line, "BEGIN:VEVENT")) {		$start_time = "";		$end_time = "";		$summary = "";		$allday_start = "";		$allday_end = "";		$start = "";		$end = "";	} else if (strstr($line, "END:VEVENT")) {				if($start_time != "")		{			//echo "<b>Start</b> $start_time <b>End</B> $end_time <b>Summary</b> $summary<br>\n";			ereg ("([0-9]{2})([0-9]{2})", $start_time, $time);			$hour = $time[1];			$minute = $time[2];									if($minute < 15)				$minute = "00";			else if($minute >=15 && $minute < 45)				$minute = "30";			else if($minute >= 45)			{				$hour = sprintf("%.02d", ($hour + 1));				$minute = "00";			}			$time_shown[($hour.$minute)]["event_start"] = $start_time;			$time_shown[($hour.$minute)]["event_text"] = $summary;		}				// Let's handle the all day fields		if($end_time != "")		{			$time_shown[($hour.$minute)]["event_end"] = $end_time;						ereg ("([0-9]{2})([0-9]{2})", $end_time, $time2);			$length = round((($time2[1]*60+$time2[2]) - ($time[1]*60+$time[2]))/30);						$time_shown[($hour.$minute)]["event_length"] = $length;		} elseif ($start_time && !$end_time) {			$time_shown[($hour.$minute)]["event_length"] = 2;		}				if ($allday_start != "") {			$start = strtotime("$allday_start");			$end = strtotime("$allday_end");			do {				if (date("Ymd", $start) == $getdate) {					$all_day[$i] = "$summary";					$i++;				}				$start = ($start + (24*3600));			} while ($start != $end);		}			} else {				$field = "";		$data = "";				sscanf($line, "%[^:]:%[^\n]", &$field, &$data);		//echo $line . "<br>";		//echo $field . "<br>";		//echo $data . "<br>";				if(strstr($field, "DTSTART;TZID")) {			//echo $data;			$data = ereg_replace("T", "", $data);			ereg ("([0-9]{4})([0-9]{2})([0-9]{2})([0-9]{0,2})([0-9]{0,2})", $data, $regs);			$year = $regs[1];			$month = $regs[2];			$day = $regs[3];			$hour = $regs[4];			$minute = $regs[5];			if($year ==