 jS", $dayofmonth);											$i = 0;																						// Pull out each day											foreach ($val as $new_val) {																								// Pull out each time												foreach ($new_val as $new_key2 => $new_val2) {												if (!$new_val2["event_text"]) {													foreach ($new_val2 as $all_day) {														$event_text = $all_day;														echo "<tr height=\"20\">\n";														echo "<td valign=\"middle\" bgcolor=\"white\" width=\"150\" height=\"20\">\n";														echo "<font class=\"G10B\">&nbsp;<a class=\"psf\" href=\"day.php?getdate=$key\">$dayofmonth</font></a>\n";														echo "</td>\n";														echo "<td width=\"1\" height=\"20\">\n";														echo "</td>\n";														echo "<td valign=\"middle\" bgcolor=\"white\" width=\"551\" height=\"20\">\n";														echo "<font class=\"G10B\">&nbsp;<a class=\"psf\" href=\"day.php?getdate=$key\">$event_text</font></a> <font class=\"V9\">(All day event)</font>\n";														echo "</td>\n";														echo "</tr>\n";													}												} elseif ($new_val2["event_text"]) {														$event_text = $new_val2["event_text"];													$event_start = $new_val2["event_start"];													$event_end = $new_val2["event_end"];													$event_start = strtotime ("$event_start");													$event_start = date ("g:i a", $event_start);													$event_end = strtotime ("$event_end");													$event_end = date ("g:i a", $event_end);													echo "<tr height=\"20\">\n";													echo "<td valign=\"middle\" bgcolor=\"white\" width=\"150\" height=\"20\">\n";													echo "<font class=\"G10B\">&nbsp;<a class=\"psf\" href=\"day.php?getdate=$key\">$dayofmonth</font></a>\n";													echo "</td>\n";													echo "<td width=\"1\" height=\"20\">\n";													echo "</td>\n";													echo "<td valign=\"middle\" bgcolor=\"white\" width=\"551\" height=\"20\">\n";													echo "<font class=\"G10B\">&nbsp;<a class=\"psf\" href=\"day.php?getdate=$key\">$event_text</a></font> <font class=\"V9\">($event_start - $event_end)</font>\n";													echo "</td>\n";													echo "</tr>\n";												}												}											}										}									}																?>							</table>						</td>					</tr>				</table>			</td>		</tr>	</table>				</td>		</tr>	</table>		<br><table width="825" border="0" cellspacing="0" cellpadding="0" class="V12">	<tr>		<td align="left" width="5%"><a class="psf" href="day.php">Today</a></td>		<td align="center" width="90%"><a class="psf" href="day.php">Day</a> | <a class="psf" href="week.php">Week</a> | <a class="psf" href="month.php">Month</a></td>		<td align="right" width="5%"><a class="psf" href="preferences.php">Preferences</a></td>	</tr>	<tr>		<td colspan="3"><img src="images/spacer.gif" height="10" width="1"></td>	</tr></table></center></body></html>